tool.command:

  ln -f -s ${input_cein_path} ${input_cein_path}.${input_cein_path.ext};

  ln -f -s ${input_ceout_path} ${input_ceout_path}.${input_ceout_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
  #end if

cestats_run

#if $config.sele == "option1":
    --config ${config.properties}.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config '__oc____dq__timestep__dq__:${properties.timestep},__dq__verbose__dq__:${properties.verbose},__dq__interval__dq__:${properties.interval},__dq__reduced__dq__:${properties.reduced},__dq__eos__dq__:${properties.eos},__dq__calceo__dq__:${properties.calceo},__dq__running_avg_window__dq__:${properties.running_avg_window},__dq__chunk_window__dq__:${properties.chunk_window},__dq__cumulative__dq__:${properties.cumulative},__dq__fix_remd__dq__:__dq__${properties.fix_remd}__dq__,__dq__conditional__dq__:__dq__${properties.conditional}__dq____cc__'
  #end if

  #if str($input_cein_path) != 'None':
    --input_cein_path ${input_cein_path}.${input_cein_path.ext}
  #end if

  #if str($input_ceout_path) != 'None':
    --input_ceout_path ${input_ceout_path}.${input_ceout_path.ext}
  #end if


  --output_dat_path $__new_file_path__/$outname_output_dat_path

  --output_population_path $__new_file_path__/$outname_output_population_path

  --output_chunk_path $__new_file_path__/$outname_output_chunk_path

  --output_cumulative_path $__new_file_path__/$outname_output_cumulative_path

  --output_conditional_path $__new_file_path__/$outname_output_conditional_path

  --output_chunk_conditional_path $__new_file_path__/$outname_output_chunk_conditional_path
;          
#if $config.sele == "option1":
    rm -f ${config.properties}.${config.properties.ext};
  #end if

  rm -f ${input_cein_path}.${input_cein_path.ext};

  rm -f ${input_ceout_path}.${input_ceout_path.ext};


  if test -f $__new_file_path__/$outname_output_dat_path; then mv $__new_file_path__/$outname_output_dat_path $output_dat_path; fi;

  if test -f $__new_file_path__/$outname_output_population_path; then mv $__new_file_path__/$outname_output_population_path $output_population_path; fi;

  if test -f $__new_file_path__/$outname_output_chunk_path; then mv $__new_file_path__/$outname_output_chunk_path $output_chunk_path; fi;

  if test -f $__new_file_path__/$outname_output_cumulative_path; then mv $__new_file_path__/$outname_output_cumulative_path $output_cumulative_path; fi;

  if test -f $__new_file_path__/$outname_output_conditional_path; then mv $__new_file_path__/$outname_output_conditional_path $output_conditional_path; fi;

  if test -f $__new_file_path__/$outname_output_chunk_conditional_path; then mv $__new_file_path__/$outname_output_chunk_conditional_path $output_chunk_conditional_path; fi;
