tool.command:

  ln -f -s ${input_pdb_path} ${input_pdb_path}_`echo $$`.${input_pdb_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

pdb4amber_run

#if $config.sele == "option1":
    --config ${config.properties}.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config '__oc____dq__remove_hydrogens__dq__:${properties.remove_hydrogens},__dq__remove_waters__dq__:${properties.remove_waters},__dq__constant_pH__dq__:${properties.constant_pH}__cc__'
  #end if

  #if str($input_pdb_path) != 'None':
    --input_pdb_path ${input_pdb_path}_`echo $$`.${input_pdb_path.ext}
  #end if


  --output_pdb_path $__new_file_path__/$outname_output_pdb_path
;          
#if $config.sele == "option1":
    rm -f ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

  rm -f ${input_pdb_path}_`echo $$`.${input_pdb_path.ext};


  if test -f $__new_file_path__/$outname_output_pdb_path; then mv $__new_file_path__/$outname_output_pdb_path $output_pdb_path; fi;
