tool.command:

  ln -f -s ${input_path} ${input_path}_`echo $$`.${input_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

acpype_params_ac

#if $config.sele == "option1":
    --config ${config.properties}.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config '__oc____dq__basename__dq__:__dq__${properties.basename}__dq__,__dq__charge__dq__:${properties.charge},__dq__acpype_path__dq__:__dq__${properties.acpype_path}__dq____cc__'
  #end if

  #if str($input_path) != 'None':
    --input_path ${input_path}_`echo $$`.${input_path.ext}
  #end if


  --output_path_frcmod $__new_file_path__/$outname_output_path_frcmod

  --output_path_inpcrd $__new_file_path__/$outname_output_path_inpcrd

  --output_path_lib $__new_file_path__/$outname_output_path_lib

  --output_path_prmtop $__new_file_path__/$outname_output_path_prmtop
;          
#if $config.sele == "option1":
    rm -f ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

  rm -f ${input_path}_`echo $$`.${input_path.ext};


  if test -f $__new_file_path__/$outname_output_path_frcmod; then mv $__new_file_path__/$outname_output_path_frcmod $output_path_frcmod; fi;

  if test -f $__new_file_path__/$outname_output_path_inpcrd; then mv $__new_file_path__/$outname_output_path_inpcrd $output_path_inpcrd; fi;

  if test -f $__new_file_path__/$outname_output_path_lib; then mv $__new_file_path__/$outname_output_path_lib $output_path_lib; fi;

  if test -f $__new_file_path__/$outname_output_path_prmtop; then mv $__new_file_path__/$outname_output_path_prmtop $output_path_prmtop; fi;
