tool.command:

  ln -f -s ${input_cmip_pdb_path} ${input_cmip_pdb_path}_`echo $$`.${input_cmip_pdb_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

ignore_residues

#if $config.sele == "option1":
    --config ${config.properties}.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config '__oc____dq__residue_list__dq__:__dq__${properties.residue_list}__dq__,__dq__ignore_all__dq__:${properties.ignore_all}__cc__'
  #end if

  #if str($input_cmip_pdb_path) != 'None':
    --input_cmip_pdb_path ${input_cmip_pdb_path}_`echo $$`.${input_cmip_pdb_path.ext}
  #end if


  --output_cmip_pdb_path $outname_output_cmip_pdb_path
;          
#if $config.sele == "option1":
    rm -f ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

  rm -f ${input_cmip_pdb_path}_`echo $$`.${input_cmip_pdb_path.ext};


  if test -f $outname_output_cmip_pdb_path; then mv $outname_output_cmip_pdb_path $output_cmip_pdb_path; fi;
