tool.command:

  ln -f -s ${input_pdb_path} ${input_pdb_path}.${input_pdb_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
  #end if

prepare_pdb

#if $config.sele == "option1":
    --config ${config.properties}.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config '__oc____dq__remove_water__dq__:${properties.remove_water},__dq__add_hydrogen__dq__:${properties.add_hydrogen},__dq__keep_hydrogen__dq__:${properties.keep_hydrogen},__dq__fix_sidechains__dq__:${properties.fix_sidechains},__dq__fix_backbone_atoms__dq__:${properties.fix_backbone_atoms}__cc__'
  #end if

  #if str($input_pdb_path) != 'None':
    --input_pdb_path ${input_pdb_path}.${input_pdb_path.ext}
  #end if


  --output_cmip_pdb_path $__new_file_path__/$outname_output_cmip_pdb_path
;          
#if $config.sele == "option1":
    rm -f ${config.properties}.${config.properties.ext};
  #end if

  rm -f ${input_pdb_path}.${input_pdb_path.ext};


  if test -f $__new_file_path__/$outname_output_cmip_pdb_path; then mv $__new_file_path__/$outname_output_cmip_pdb_path $output_cmip_pdb_path; fi;
