tool.command:

  ln -f -s ${input_tpr_path} ${input_tpr_path}.${input_tpr_path.ext};

  ln -f -s ${input_cpt_path} ${input_cpt_path}.${input_cpt_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
  #end if

mdrun

#if $config.sele == "option1":
    --config ${config.properties}.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config "__oc____dq__mpi_bin__dq__:__dq__${properties.mpi_bin}__dq__,__dq__mpi_np__dq__:${properties.mpi_np},__dq__mpi_flags__dq__:__dq__${properties.mpi_flags}__dq__,__dq__checkpoint_time__dq__:${properties.checkpoint_time},__dq__num_threads__dq__:${properties.num_threads},__dq__num_threads_mpi__dq__:${properties.num_threads_mpi},__dq__num_threads_omp__dq__:${properties.num_threads_omp},__dq__num_threads_omp_pme__dq__:${properties.num_threads_omp_pme},__dq__use_gpu__dq__:${properties.use_gpu},__dq__gpu_id__dq__:__dq__${properties.gpu_id}__dq__,__dq__gpu_tasks__dq__:__dq__${properties.gpu_tasks}__dq__,__dq__gmx_lib__dq__:__dq__${properties.gmx_lib}__dq__,__dq__gmx_path__dq__:__dq__${properties.gmx_path}__dq____cc__"
  #end if

  #if str($input_tpr_path) != 'None':
    --input_tpr_path ${input_tpr_path}.${input_tpr_path.ext}
  #end if

  #if str($input_cpt_path) != 'None':
    --input_cpt_path ${input_cpt_path}.${input_cpt_path.ext}
  #end if


  --output_trr_path $__new_file_path__/$outname_output_trr_path

  --output_gro_path $__new_file_path__/$outname_output_gro_path

  --output_edr_path $__new_file_path__/$outname_output_edr_path

  --output_log_path $__new_file_path__/$outname_output_log_path

  --output_xtc_path $__new_file_path__/$outname_output_xtc_path

  --output_cpt_path $__new_file_path__/$outname_output_cpt_path

  --output_dhdl_path $__new_file_path__/$outname_output_dhdl_path
;          
#if $config.sele == "option1":
    rm -f ${config.properties}.${config.properties.ext};
  #end if

  rm -f ${input_tpr_path}.${input_tpr_path.ext};

  rm -f ${input_cpt_path}.${input_cpt_path.ext};


  if test -f $__new_file_path__/$outname_output_trr_path; then mv $__new_file_path__/$outname_output_trr_path $output_trr_path; fi;

  if test -f $__new_file_path__/$outname_output_gro_path; then mv $__new_file_path__/$outname_output_gro_path $output_gro_path; fi;

  if test -f $__new_file_path__/$outname_output_edr_path; then mv $__new_file_path__/$outname_output_edr_path $output_edr_path; fi;

  if test -f $__new_file_path__/$outname_output_log_path; then mv $__new_file_path__/$outname_output_log_path $output_log_path; fi;

  if test -f $__new_file_path__/$outname_output_xtc_path; then mv $__new_file_path__/$outname_output_xtc_path $output_xtc_path; fi;

  if test -f $__new_file_path__/$outname_output_cpt_path; then mv $__new_file_path__/$outname_output_cpt_path $output_cpt_path; fi;

  if test -f $__new_file_path__/$outname_output_dhdl_path; then mv $__new_file_path__/$outname_output_dhdl_path $output_dhdl_path; fi;
