tool.command:

  ln -f -s ${input_solute_gro_path} ${input_solute_gro_path}.${input_solute_gro_path.ext};

  ln -f -s ${input_top_zip_path} ${input_top_zip_path}.${input_top_zip_path.ext};

  ln -f -s ${input_solvent_gro_path} ${input_solvent_gro_path}.${input_solvent_gro_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
  #end if

solvate

#if $config.sele == "option1":
    --config ${config.properties}.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config "__oc____dq__shell__dq__:${properties.shell},__dq__gmx_lib__dq__:__dq__${properties.gmx_lib}__dq__,__dq__gmx_path__dq__:__dq__${properties.gmx_path}__dq____cc__"
  #end if

  #if str($input_solute_gro_path) != 'None':
    --input_solute_gro_path ${input_solute_gro_path}.${input_solute_gro_path.ext}
  #end if

  #if str($input_top_zip_path) != 'None':
    --input_top_zip_path ${input_top_zip_path}.${input_top_zip_path.ext}
  #end if

  #if str($input_solvent_gro_path) != 'None':
    --input_solvent_gro_path ${input_solvent_gro_path}.${input_solvent_gro_path.ext}
  #end if


  --output_gro_path $__new_file_path__/$outname_output_gro_path

  --output_top_zip_path $__new_file_path__/$outname_output_top_zip_path
;          
#if $config.sele == "option1":
    rm -f ${config.properties}.${config.properties.ext};
  #end if

  rm -f ${input_solute_gro_path}.${input_solute_gro_path.ext};

  rm -f ${input_top_zip_path}.${input_top_zip_path.ext};

  rm -f ${input_solvent_gro_path}.${input_solvent_gro_path.ext};


  if test -f $__new_file_path__/$outname_output_gro_path; then mv $__new_file_path__/$outname_output_gro_path $output_gro_path; fi;

  if test -f $__new_file_path__/$outname_output_top_zip_path; then mv $__new_file_path__/$outname_output_top_zip_path $output_top_zip_path; fi;
