neural_network_decode



  #if str($input_decode_path) != 'None':
    --input_decode_path ${input_decode_path}_`echo $$`.${input_decode_path.ext}
  #end if

  #if str($input_model_path) != 'None':
    --input_model_path ${input_model_path}_`echo $$`.${input_model_path.ext}
  #end if


  --output_decode_path $__new_file_path__/$outname_output_decode_path

  --output_predict_path $__new_file_path__/$outname_output_predict_path
;          


  rm -f ${input_decode_path}_`echo $$`.${input_decode_path.ext};

  rm -f ${input_model_path}_`echo $$`.${input_model_path.ext};


  if test -f $__new_file_path__/$outname_output_decode_path; then mv $__new_file_path__/$outname_output_decode_path $output_decode_path; fi;

  if test -f $__new_file_path__/$outname_output_predict_path; then mv $__new_file_path__/$outname_output_predict_path $output_predict_path; fi;
