tool.command:

  ln -f -s ${input_structure_path} ${input_structure_path}.${input_structure_path.ext};

  ln -f -s ${input_b_structure_path} ${input_b_structure_path}.${input_b_structure_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
  #end if

pmxmutate

#if $config.sele == "option1":
    --config ${config.properties}.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config "__oc____dq__mutation_list__dq__:__dq__${properties.mutation_list}__dq__,__dq__force_field__dq__:__dq__${properties.force_field}__dq__,__dq__resinfo__dq__:${properties.resinfo},__dq__gmx_lib__dq__:__dq__${properties.gmx_lib}__dq__,__dq__pmx_path__dq__:__dq__${properties.pmx_path}__dq____cc__"
  #end if

  #if str($input_structure_path) != 'None':
    --input_structure_path ${input_structure_path}.${input_structure_path.ext}
  #end if

  #if str($input_b_structure_path) != 'None':
    --input_b_structure_path ${input_b_structure_path}.${input_b_structure_path.ext}
  #end if


  --output_structure_path $__new_file_path__/$outname_output_structure_path
;          
#if $config.sele == "option1":
    rm -f ${config.properties}.${config.properties.ext};
  #end if

  rm -f ${input_structure_path}.${input_structure_path.ext};

  rm -f ${input_b_structure_path}.${input_b_structure_path.ext};


  if test -f $__new_file_path__/$outname_output_structure_path; then mv $__new_file_path__/$outname_output_structure_path $output_structure_path; fi;
