tool.command:

  ln -f -s ${input_structure_path} ${input_structure_path}_`echo $$`.${input_structure_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

extract_heteroatoms

#if $config.sele == "option1":
    --config ${config.properties}.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config '__oc____dq__heteroatoms__dq__:${properties.heteroatoms},__dq__water__dq__:${properties.water}__cc__'
  #end if

  #if str($input_structure_path) != 'None':
    --input_structure_path ${input_structure_path}_`echo $$`.${input_structure_path.ext}
  #end if


  --output_heteroatom_path $outname_output_heteroatom_path
;          
#if $config.sele == "option1":
    rm -f ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

  rm -f ${input_structure_path}_`echo $$`.${input_structure_path.ext};


  if test -f $outname_output_heteroatom_path; then mv $outname_output_heteroatom_path $output_heteroatom_path; fi;
