tool.command:

  ln -f -s ${input_structure_path} ${input_structure_path}_`echo $$`.${input_structure_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

renumber_structure

#if $config.sele == "option1":
    --config ${config.properties}.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config '__oc____dq__renumber_residues__dq__:${properties.renumber_residues},__dq__renumber_residues_per_chain__dq__:${properties.renumber_residues_per_chain}__cc__'
  #end if

  #if str($input_structure_path) != 'None':
    --input_structure_path ${input_structure_path}_`echo $$`.${input_structure_path.ext}
  #end if


  --output_structure_path $outname_output_structure_path

  --output_mapping_json_path $outname_output_mapping_json_path
;          
#if $config.sele == "option1":
    rm -f ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

  rm -f ${input_structure_path}_`echo $$`.${input_structure_path.ext};


  if test -f $outname_output_structure_path; then mv $outname_output_structure_path $output_structure_path; fi;

  if test -f $outname_output_mapping_json_path; then mv $outname_output_mapping_json_path $output_mapping_json_path; fi;
