tool.command:

  ln -f -s ${input_pdb_path} ${input_pdb_path}.${input_pdb_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
  #end if

fpocket_run

#if $config.sele == "option1":
    --config ${config.properties}.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config "__oc____dq__min_radius__dq__:${properties.min_radius},__dq__max_radius__dq__:${properties.max_radius},__dq__num_spheres__dq__:${properties.num_spheres},__dq__sort_by__dq__:__dq__${properties.sort_by}__dq__,__dq__fpocket_path__dq__:__dq__${properties.fpocket_path}__dq____cc__"
  #end if

  #if str($input_pdb_path) != 'None':
    --input_pdb_path ${input_pdb_path}.${input_pdb_path.ext}
  #end if


  --output_pockets_zip $__new_file_path__/$outname_output_pockets_zip

  --output_summary $__new_file_path__/$outname_output_summary
;          
#if $config.sele == "option1":
    rm -f ${config.properties}.${config.properties.ext};
  #end if

  rm -f ${input_pdb_path}.${input_pdb_path.ext};


  if test -f $__new_file_path__/$outname_output_pockets_zip; then mv $__new_file_path__/$outname_output_pockets_zip $output_pockets_zip; fi;

  if test -f $__new_file_path__/$outname_output_summary; then mv $__new_file_path__/$outname_output_summary $output_summary; fi;
